import { Entity } from '../index.js';
/**
 * 상점 부담 무이자 할부 옵션
 * 예) 삼성카드에 대해 2, 3개월 무이자 할부 적용, 신한카드에 대해 2, 3, 4, 5, 6개월 무이자 할부 적용
 * freeInstallmentPlans: [
 *  {
 *    cardCompany: 'CARD_COMPANY_SAMSUNG',
 *    months: [2, 3]
 *  },
 *  {
 *    cardCompany: 'CARD_COMPANY_SHINHAN',
 *    months: [2, 3, 4, 5, 6]
 *  }
 * ]
 */
export type FreeInstallmentPlan = {
    cardCompany: Entity.CardCompany;
    months: number[];
};
