/**
 * 정규화가 어려운 토스페이 bypass 파라미터
 * 가맹점으로부터 전달 된 값을 PG사로 그대로 전달
 */
export type TosspayV2PaymentBypass = {
    /**
     * 결제 만료 기한 (yyyy-MM-dd HH:mm:ss)
     */
    expiredTime?: string;
    /**
     * 현금영수증 발급타입
     * CULTURE : 문화비
     * GENERAL : 일반 (default)
     * PUBLIC_TP 교통비
     */
    cashReceiptTradeOption?: 'CULTURE' | 'GENERAL' | 'PUBLIC_TP';
};
